mapper.select:
SELECT USER_ID, AMOUNT, REMAIN_COIN, TYPE, TRANSACTION_DATE, QUEST_ID
FROM COIN_HISTORY
WHERE USER_ID = #{userId}

SELECT PAYMENT_HISTORY_ID, USER_ID, PAYMENT_DATE, AMOUNT, REMAIN_COIN, TYPE, STATUS
FROM PAYMENT_HISTORY
WHERE USER_ID = #{userId}

mapper.update:
UPDATE USERS
SET COIN_COUNT = COIN_COUNT + #{coinAmount}
WHERE USER_ID = #{userId}

UPDATE USERS
SET COIN_COUNT = COIN_COUNT + #{coinAmount}
WHERE USER_ID = #{userId}
